              $('#category-products').width(width_owl_active_tab);
          }
      }
      $('#category-products').owlCarousel({
                  direction:'ltr',
                items : 4,
        itemsCustom : false,
        itemsDesktop : [1200, 4],
        itemsDesktopSmall : [992, 3],
